import { createStore } from "vuex";
// import { reactive } from "vue";

export default createStore({
  state: {
    arr: [],
  },
  getters: {},
  mutations: {
    setArr(state, arrs) {
      state.arr = arrs;
    },
  },
  actions: {
    HandleSubmitForm() {},
  },
  modules: {},
});
