import { createStore } from "vuex";

export default createStore({
  state: {
    arr: [],
    d:0,
    infomation: {
      id: "",
      name: "",
      blog: "",
      title: "",
      age: 20,
      Category: [],
      gender: "",
    },
  },
  getters: {},
  mutations: {
    SAVE_DATA(state, item) {
      state.arr.push(item);
    },
    DELETE_DATA(state, item) {
      state.arr = state.arr.filter((i) => i.id !== item);
    },
    EDIT(state, item) {

      state.infomation.name = item.name;
      state.infomation.blog = item.blog;
      state.infomation.title = item.title;
      state.infomation.age = item.age;
      state.infomation.Category = item.Category;
      state.infomation.gender = item.gender;
    },
    // UPDATE(state, item){
    //   state.arr[item]=state.infomation
    // },
    C(state, item){
      state.d=item;
    }
  },
  actions: {
    savedata({ commit }, item) {
      commit("SAVE_DATA", item);
    },
    delete({ commit }, item) {
      commit("DELETE_DATA", item);
    },
    edit({ commit }, item) {
      commit("EDIT", item);
    },
    update({ commit }, item) {
      commit("UPDATE", item);
    },
    c({ commit }, item) {
      commit("C", item);
    },
  },
  modules: {},
});
