import { createStore } from "vuex";


export default createStore({
  state: {
    arr: [],

  },
  getters: {},
  mutations: {
    SAVE_DATA(state, item) {
      state.arr.push(item);
    },
    DELETE(state,item){
      state.arr.filter((i)=>i.id !== item);
    }


  },
  actions: {
    savedata({ commit }, item) {
      commit("SAVE_DATA", item);
    },
    delete({commit},item){
      commit('DELETE',item)
    }
  },
  modules: {},
});
